SELECT LPAD('ã„´', LVL*4, ' ') || B.MENU_NM AS MENU_LVL , B.MENU_NM , B.LIC_GRP_ID , A.AUTH_GRP_ID , B.MENU_ID , B.UPPER_MENU_ID , SF_STM2000_MENU_NM(B.LIC_GRP_ID, B.MENU_ID, '2') AS UP_MENU_NM , (SELECT UPPER_MENU_ID FROM STM2000 Z WHERE Z.LIC_GRP_ID = B.LIC_GRP_ID AND Z.MENU_ID = B.UPPER_MENU_ID) AS TWO_UPPER_MENU_ID , SF_STM2000_MENU_NM(B.LIC_GRP_ID, B.MENU_ID, '3') AS UPUP_MENU_NM , LVL , B.ORD , B.MENU_DESC , B.MENU_PATH , B.MENU_TYPE_CD , B.MENU_URL , B.MENU_IMG_URL , B.MENU_ICON , B.MODULE_TYPE , NVL(A.MAIN_YN,'N') as MAIN_YN , A.ACCESS_YN , A.SELECT_YN , A.REG_YN , A.MODIFY_YN , A.DEL_YN , A.EXCEL_YN , A.PRINT_YN , A.USE_CD , ( SELECT ACCEPT_USE_CD FROM PRJ2000 WHERE 1=1 AND AUTH_GRP_ID = #authGrpId# AND LIC_GRP_ID = #licGrpId# AND PRJ_ID = #prjId# ) AS ACCEPT_USE_CD FROM ( SELECT * FROM STM2001 WHERE 1=1 AND LIC_GRP_ID = #licGrpId# AND PRJ_ID = #prjId# AND ACCESS_YN = 'Y' AND AUTH_GRP_ID = #authGrpId# ) A , (SELECT * FROM STM2000 WHERE LIC_GRP_ID = #licGrpId# AND USE_CD = '01'
AND SYS_CD = '02'
) B , (SELECT * FROM PRJ2001 WHERE USR_ID = #usrId#) C WHERE 1=1 AND C.PRJ_ID = A.PRJ_ID AND C.AUTH_GRP_ID = A.AUTH_GRP_ID AND A.LIC_GRP_ID = B.LIC_GRP_ID AND A.MENU_ID = B.MENU_ID AND (B.PRJ_TYPE = #prjTypeCd# OR B.PRJ_TYPE = '03') CONNECT BY PRIOR B.MENU_ID = B.UPPER_MENU_ID START WITH B.MENU_ID = '000'
