sqlMap.insert:
SELECT NVL( SUBSTR(NEW_PRJ_GRP_ID, 1, 11) || LPAD( (TO_NUMBER(SUBSTR(NEW_PRJ_GRP_ID, 12, 5)) + 1) , 5, '0') , 'GRP' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '00001' ) AS NEW_PRJ_GRP_ID FROM ( SELECT MAX(PRJ_ID) AS NEW_PRJ_GRP_ID FROM PRJ1000 A WHERE 1=1 AND A.PRJ_ID LIKE 'GRP' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '%' AND A.PRJ_GRP_CD = '01' )
/* prj1000DAO.insertPrj1000PrjGrpAjax - 프로젝트 그룹 생성 */
INSERT INTO PRJ1000 ( PRJ_ID , PRJ_GRP_ID , LIC_GRP_ID , PRJ_NM , START_DT , END_DT , ORD , PRJ_DESC , USE_CD , PRJ_TYPE_CD , PRJ_DEV_TYPE_CD , PRJ_GRP_CD , REG_DTM , REG_USR_ID , REG_USR_IP , MODIFY_DTM , MODIFY_USR_ID , MODIFY_USR_IP )VALUES( #newPrjGrpId# , NULL , #licGrpId# , #prjNm# , REPLACE(#startDt#, '-', '') , REPLACE(#endDt#, '-', '') , #ord# , #prjDesc# , #useCd# , #prjTypeCd# , #prjDevTypeCd# , #prjGrpCd# , SYSDATE , #regUsrId# , #regUsrIp# , SYSDATE , #regUsrId# , #regUsrIp# )

sqlMap.select:
/* prj1000DAO.selectPrj1000ProjectGroupListAjax - 라이선스 그룹에 속해있는 모든 프로젝트를 가져온다. */ SELECT Z.*, CONNECT_BY_ISLEAF AS LEAF FROM( SELECT A.PRJ_ID , A.PRJ_GRP_ID , NVL(A.PRJ_GRP_ID, '000') AS UPPER_ID , A.LIC_GRP_ID , A.PRJ_NM , TO_CHAR(TO_DATE(A.START_DT,'YYYYMMDD'),'YYYY-MM-DD') AS START_DT , TO_CHAR(TO_DATE(A.END_DT,'YYYYMMDD'),'YYYY-MM-DD') AS END_DT , A.ORD , A.PRJ_DESC , A.USE_CD , A.PRJ_TYPE , SF_STM4001_COM_CD_INFO(#licGrpId#, 'PRJ00013', A.PRJ_TYPE, '1') AS PRJ_TYPE_NM , A.PRJ_ACRM , A.PRJ_GRP_CD , A.REG_DTM , A.REG_USR_ID , A.REG_USR_IP , A.MODIFY_DTM , A.MODIFY_USR_ID , A.MODIFY_USR_IP FROM PRJ1000 A WHERE 1=1 AND A.LIC_GRP_ID = #licGrpId# AND A.USE_CD = '01' /* 사용유무가 사용인 프로젝트만 조회 */
AND A.PRJ_GRP_CD = '01' /* 프로젝트 그룹만 조회 */
) Z CONNECT BY PRIOR Z.PRJ_ID = Z.PRJ_GRP_ID START WITH Z.PRJ_GRP_ID IS NULL ORDER SIBLINGS BY Z.ORD, Z.PRJ_NM

