SELECT LPAD('ã„´', LVL*4, ' ') || A.MENU_NM AS MENU_LVL , A.MENU_NM , A.LIC_GRP_ID , A.MENU_ID , A.UPPER_MENU_ID , SF_STM2000_MENU_NM(A.LIC_GRP_ID, A.MENU_ID, '2') AS UP_MENU_NM , (SELECT UPPER_MENU_ID FROM STM2000 Z WHERE Z.LIC_GRP_ID = A.LIC_GRP_ID AND Z.MENU_ID = A.UPPER_MENU_ID) AS TWO_UPPER_MENU_ID , SF_STM2000_MENU_NM(A.LIC_GRP_ID, A.MENU_ID, '3') AS UPUP_MENU_NM , A.LVL , A.ORD , A.USE_CD , A.MENU_DESC , A.MENU_PATH , A.MENU_TYPE_CD , A.MENU_URL , A.MENU_IMG_URL , A.MENU_ICON , '' AS ROW_STATUS , A.MODULE_TYPE , A.REG_DTM , A.REG_USR_ID , A.REG_USR_IP , A.MODIFY_DTM , A.MODIFY_USR_ID , A.MODIFY_USR_IP FROM (SELECT * FROM STM2000 WHERE LIC_GRP_ID = #licGrpId#
AND SYS_CD = '02'
) A WHERE 1=1 CONNECT BY PRIOR A.MENU_ID = A.UPPER_MENU_ID START WITH A.MENU_ID = '000' ORDER BY ORD ASC
